                        break;
                    case 'passwd':
                        _this.getErrorInfo("#passwd", _this.checkPasswd());
                        break
                }
            });
            $(".btn-captcha").click(function () {
                _this.checkEnroll()
            });
            $("#captcha").keydown(function (e) {
                if (e.which == '13') {
                    $("#captcha").blur();
                    $("#submit-phone").click()
                }
            });
            $("#passwd").keydown(function (e) {
                if (e.which == '13') {
                    $("#passwd").blur();
                    $("#submit-passwd").click()
                }
            });
